UPDATE dbo.vigie_ctrl
SET status_global = 'IN_PROGRESS'
WHERE ctrl_id = @ctrl_id;

DECLARE @start_ts datetime2;
DECLARE @end_ts datetime2;
DECLARE @duration_sec int;

SET @end_ts = SYSUTCDATETIME();

SELECT
    @start_ts = synapse_start_ts
FROM dbo.vigie_ctrl
WHERE ctrl_id = @ctrl_id;

IF @start_ts IS NULL
BEGIN
    UPDATE dbo.vigie_ctrl
    SET
        synapse_end_ts = @end_ts,
        synapse_duration_sec = NULL
    WHERE ctrl_id = @ctrl_id;
    RETURN;
END;

SET @duration_sec = DATEDIFF(SECOND, @start_ts, @end_ts);

UPDATE dbo.vigie_ctrl
SET
    synapse_end_ts = @end_ts,
    synapse_duration_sec = @duration_sec
WHERE ctrl_id = @ctrl_id;

